import { useState } from "react";
import { HeadingOne } from "../components/Modules";

//This is the EDITOR

const index = () => {
  const [body, setBody] = useState([]);

  const buildBody = (value) => {
    setBody((body) => [...body, value]);
  };

  return (
    <>
      {body.map((node) => {
        return node;
      })}
    </>
  );
};

export default index;
