import { useState } from "react";
import { HeadingOne } from "../components/Modules";

//This is the EDITOR

const index = () => {
  const [body, setBody] = useState([]);
  const [value, setValue] = useState();

  const buildBody = (node) => {
    setBody((body) => [...body, node]);
  };

  const addHeadingOne = (e) => {
    e.preventDefault();
    buildBody(<HeadingOne value={value} />);
  };

  const handleChange = (string) => {
    setValue(string);
  };

  return (
    <>
      {body.map((node) => {
        return node;
      })}
      <form onSubmit={addHeadingOne}>
        <input type="text" onChange={handleChange} />
        <input type="submit" />
      </form>
    </>
  );
};

export default index;
