import { useState } from "react";

//This is the EDITOR

const index = () => {
  const [value, setValue] = useState("Title...");
  return (
    <>
      <h1
        contentEditable
        suppressContentEditableWarning
        onInput={() => {
          setValue(this.textContent);
        }}
      >
        {value}
      </h1>
    </>
  );
};

export default index;
