import { useState } from "react";
import { HeadingOne } from "../components/Modules";

//This is the EDITOR

const index = () => {
  const [body, setBody] = useState([]);

  const buildBody = (value) => {
    setBody((body) => [...body, value]);
  };

  const addHeadingOne = (value) => {
    <HeadingOne value={value} />;
  };

  return (
    <>
      {body.map((node) => {
        return node;
      })}
      <form onSubmit={addHeadingOne}>
        <input type="text" />
        <input type="submit" />
      </form>
    </>
  );
};

export default index;
